import { CollectionQuery, PreparedInclusions, PreparedOrder, PreparedOrderStatement, PreparedQuery, PreparedWhere, PreparedWhereFilter, QueryAfter } from '../types.js';
import { Value } from '../codec.js';
/**
 * Hashes a query object to a unique string, ignoring non-query properties. Thus the hash is of the query the server will see.
 */
export declare function hashQuery<Q extends CollectionQuery>(params: Q): string;
/**
 * Returns a hash of the query that is stable across varying ordering of inputs (like select and where)
 */
export declare function hashPreparedQuery(query: PreparedQuery): number;
export declare function hashFilter(filter: PreparedWhereFilter): {
    hash: number;
    sortKey: string;
};
export declare function hashFilters(filters: PreparedWhere): number;
export declare function hashOrder(order: PreparedOrder): number;
export declare function hashOrderStatement(order: PreparedOrderStatement): number;
export declare function hashSelect(select: string[]): number;
export declare function hashInclusions(include: PreparedInclusions): number;
export declare function hashAfter(after: QueryAfter): number;
export declare function hashLimit(limit: number): number;
export declare function encodeHashValue(value: Value | undefined): string;
