export class MemoryHandler {
    // Array to store log records in memory
    logs = [];
    log(record) {
        // Store the log record in the array
        this.logs.push(record);
    }
    startSpan(name, context, attributes) {
        this.logs.push(`Starting span "${name}" in context="${context ?? ''}" ${attributes ? JSON.stringify(attributes) : ''}`);
        return { name, context, attributes, startTime: Date.now() };
    }
    endSpan(span) {
        if (!span)
            return;
        const durationMs = Date.now() - span.startTime;
        this.logs.push(`Ending span "${span.name}". Duration: ${durationMs}ms`);
    }
    recordMetric(name, value, attributes) {
        this.logs.push(`Metric [${name}]: ${value} ${attributes ? JSON.stringify(attributes) : ''}`);
    }
}
